$(document).ready(function(){
    $('.contact_submit').click(function(event){
        
        console.log('event clicked')

        var name = $('.contact_name').val()
        var email = $('.contact_email').val()
        var message = $('.contact_message').val()
        var name_status = $('.name_status')
        var email_status = $('.email_status')
        var message_status = $('.message_status')
        name_status.empty()
        email_status.empty()
        message_status.empty()

        if(name.length >= 1){
            // name_status.append('<div>Name is valid</div>')
        }else{
            event.preventDefault()
            name_status.append('<div style="color:red;">Name is not valid</div>')
        }

        if(email.length >= 5 && email.includes('@') && email.includes('.')){
            // email_status.append('<div>Email is valid</div>')
        }else{
            event.preventDefault()
            email_status.append('<div style="color:red;">Email is not valid</div>')
        }

        if(message.length >= 1){
            // message_status.append('<div>Message is valid</div>')
        }else{
            event.preventDefault()
            message_status.append('<div style="color:red;">Message is not valid</div>')
        }

    })

})